final class BoringBN {
    let bignum: UnsafeMutablePointer<BIGNUM>
    
    init(bignum: UnsafeMutablePointer<BIGNUM>) {
        self.bignum = bignum
    }
    
    init() {
        self.bignum = CCryptoBoringSSL_BN_new()
    }
    
    init(arbitraryInt: ArbitraryInt) {
        var bn: UnsafeMutablePointer<BIGNUM>!
        CCryptoBoringSSL_BN_hex2bn(&bn, arbitraryInt.hexEncodedString())
        self.bignum = bn
    }
    
    deinit {
        CCryptoBoringSSL_BN_free(bignum)
    }
}

