final class swift_crypto_additionsTests: XCTestCase {
    func testRSASecret() {
        let serverPrivateKey = Insecure.RSA.Signing.PrivateKey()
        let clientPrivateKey = Insecure.RSA.Signing.PrivateKey()
        
        let serverPublicKey = serverPrivateKey.formPublicKey(generator: .generator2)
        let clientPublicKey = clientPrivateKey.formPublicKey(generator: .generator2)
        
        let serverSecret = serverPrivateKey.formSecret(with: clientPublicKey)
        let clientSecret = clientPrivateKey.formSecret(with: serverPublicKey)
        
        XCTAssertEqual(serverSecret.asArbitraryInt(), clientSecret.asArbitraryInt())
        
        // Does this match BoringSSL
        let boringSslSecret = CCryptoBoringSSL_BN_new()
        let ctx = CCryptoBoringSSL_BN_CTX_new()
        defer {
            CCryptoBoringSSL_BN_free(boringSslSecret)
            CCryptoBoringSSL_BN_CTX_free(ctx)
        }
        
//        let boringSslGenerator = BoringBN(arbitraryInt: 2)
        let boringSslPrime = BoringBN(arbitraryInt: .diffieHellmanGroup14)
//        let boringSslServerPrivateKey = BoringBN(arbitraryInt: serverPrivateKey.privateExponent)
        let boringSslServerPublicKey = BoringBN(arbitraryInt: serverPublicKey.publicExponent)
        let boringSslClientPrivateKey = BoringBN(arbitraryInt: clientPrivateKey.privateExponent)
//        let boringSslClientPublicKey = BoringBN(arbitraryInt: clientPublicKey.publicExponent)
        
        CCryptoBoringSSL_BN_mod_exp(
            boringSslSecret,
            boringSslServerPublicKey.bignum,
            boringSslClientPrivateKey.bignum,
            boringSslPrime.bignum,
            ctx
        )
        
        XCTAssertTrue(CCryptoBoringSSL_BN_equal_consttime(
            boringSslSecret,
            BoringBN(arbitraryInt: serverSecret.asArbitraryInt()).bignum
        ) == 1)
    }
    
    func testRSA2048TestVectors() throws {
        // n
        let modulus = ArbitraryInt("")!

        // d
        let privateExponent = ArbitraryInt("")!

        // e
//        let generator: ArbitraryInt = 17
        
        let privateKey = Insecure.RSA.Signing.PrivateKey(
            privateExponent: privateExponent,
            modulus: modulus
        )
        
        for vector in rsa2048SHA512TestVectors {
            XCTAssertEqual(privateKey.sign(vector.message), vector.signature)
        }
    }
    
    func testSPKIParse() throws {
        let rawRSAPubkeyDER = Data(base64Encoded: "")!
        let rawPKCS8RSAPubkeyDER = Data(base64Encoded: "")!
        let expectedNValue = ArbitraryInt("")
        let expectedEValue = ArbitraryInt(65537)
        
        let parsedRSAPubkey = try XCTUnwrap(Insecure.RSA.Signing.PublicKey(withDERBytes: rawRSAPubkeyDER))
        
        XCTAssertEqual(parsedRSAPubkey.modulus, expectedNValue)
        XCTAssertEqual(parsedRSAPubkey.publicExponent, expectedEValue)
        
        let parsedPKCS8RSAPubkey = try XCTUnwrap(Insecure.RSA.Signing.PublicKey(withDERBytes: rawPKCS8RSAPubkeyDER))
        
        XCTAssertEqual(parsedPKCS8RSAPubkey.modulus, expectedNValue)
        XCTAssertEqual(parsedPKCS8RSAPubkey.publicExponent, expectedEValue)
    }
    
